import { createContext, useContext, useState } from "react";
import Button from '../components/Button'
import { SearchContext } from "./Search";

export const LoadContext = createContext()
LoadContext.displayName = "Load More"

const LoadContextProvider = ({ children }) => {
  const [counts, setCounts] = useState({
    total_pages: 14,
    total_results: 262
  });

  return (
    <LoadContext.Provider value={{ counts, setCounts }}>
      {children}
    </LoadContext.Provider>
  )
}

export const useContextLoad = () => {
  const { counts, setCounts } = useContext(LoadContext)
  const { currentPage, setCurrentPage } = useContext(SearchContext)

  const hasPage = counts.total_pages > currentPage;

  const loadMoreItems = () => {
    if (hasPage) setCurrentPage((page) => page + 1)
  };

  return {
    counts,
    setCounts,
    loadMoreItems,
    hasPage
  }
}

export default LoadContextProvider
