import { useEffect, useState, useContext } from 'react';
import ListMovies from "./ListMovies"
import Box from '../../components/BoxFlex'
import Button from '../../components/Button'
import axios from 'axios'

import { SearchContext } from '../../contexts/Search'


const Movies = () => {
  const API_URL = "https://api.themoviedb.org/3/";
  const API_KEY = "e9da1b9b1bf2935bf963f9c98fd51e01";

  const { value, currentPage, setCurrentPage } = useContext(SearchContext)
  const [data, setData] = useState([])
  const [counts, setCounts] = useState({
    total_pages: 14,
    total_results: 262
  });

  const hasPage = counts.total_pages > currentPage;

  const loadMoreItems = () => {
    if (hasNext) setCurrentPage((page) => page + 1)
  };

  useEffect(() => {
    const endpoint = async () => {
      const resp = await axios(
        `${API_URL}movie/upcoming?api_key=${API_KEY}&language=en-US&page=${currentPage}`
      )
      console.log(resp.data.results, 'caraio')
      setData(resp.data.results)
    }

    endpoint()

  }, [currentPage])

  console.log(data, 'cade')

  return (
    <>
      <Box>
        {
          data.map(movies => (
            <ListMovies {...movies} key={movies.id} />
          ))
        }
        <Button>Load More Movies</Button>
      </Box>
    </>
  )
}

export default Movies
