import { useEffect, useState, useContext } from 'react';
import ListMovies from "./ListMovies"
import Box from '../../components/BoxFlex'
import Button from '../../components/Button'
import Results from '../../components/Results'
import Loading from '../../components/Loading'


import { SearchContext } from '../../contexts/Search'


const Movies = () => {
  const API_URL = "https://api.themoviedb.org/3/";
  const API_KEY = "e9da1b9b1bf2935bf963f9c98fd51e01";

  const { value, currentPage, setCurrentPage } = useContext(SearchContext)
  const [data, setData] = useState([])
  const [counts, setCounts] = useState({
    total_pages: 14,
    total_results: 262
  });
  const [loading, setLoading] = useState(true)

  const hasPage = counts.total_pages > currentPage;

  const loadMoreItems = () => {
    if (hasPage) setCurrentPage((page) => page + 1)
  };

  useEffect(() => {
    const endpoint =
      value === ""
        ? `${API_URL}movie/upcoming?api_key=${API_KEY}&language=en-US&page=${currentPage}`
        : `${API_URL}search/movie?api_key=${API_KEY}&language=en-US&query=${encodeURIComponent(
          value
        )}&page=${currentPage}`

    setLoading(true);
    fetch(endpoint)
      .then((response) => response.json())
      .then((json) => {

        setData((previous) =>
          currentPage === 1 ? json.results : [...previous, ...json.results]
        );

        setCounts({
          total_pages: json.total_pages,
          total_results: json.total_results
        });
      })
      .catch((error) => console.error("Error:", error))
      .finally(() => setLoading(false));

  }, [value, currentPage])

  return (
    <>
      <Results>
        {value ? `Resultados para: ${value}` : "Últimos lançamentos"}
      </Results>
      <Box>
        {
          data.map(movies => (
            <ListMovies {...movies} key={movies.id} />
          ))
        }
        {loading && '.....'}
        <>
          {hasPage ? (
            <Button onClick={loadMoreItems}>
              Load More
            </Button>
          ) : (
            <Results>
              Mostrando {counts.total_results} de {counts.total_results} Movies
            </Results>
          )}
        </>
      </Box>
    </>
  )
}

export default Movies
